function atualizarTabela(){$.getJSON(_baseUrl+"/api/v1/gateways/cielo").done((function(data){const setCheckboxState=(selector,state)=>{$(selector).prop("checked",state)};$("#desenvolvimento").toggle(1!=data.status),$("#producao").toggle(1==data.status),setCheckboxState("#activePix",1==data.active_pix),setCheckboxState("#activeCredito",1==data.active_credito),setCheckboxState("#activeDebito",1==data.active_debito),setCheckboxState("#activeBoletos",1==data.active_boletos),$("#merchantid_pro").val(data.merchantid_pro),$("#merchantkey_pro").val(data.merchantkey_pro),$("#merchantid_dev").val(data.merchantid_dev),$("#merchantkey_dev").val(data.merchantkey_dev)})).fail((function(jqXHR,textStatus,errorThrown){console.error("Erro ao carregar dados: ",textStatus,errorThrown)}))}$(document).ready((function(){atualizarTabela(),$("#formCad").ajaxForm({beforeSubmit:function(formData,jqForm,options){},success:function(responseText,statusText,xhr,$form){atualizarTabela(),$("#formCad")[0].reset(),Swal.fire({title:"Cadastrado!",icon:"success",confirmButtonClass:"btn btn-primary w-xs mt-2",buttonsStyling:!1})},error:function(xhr,status,error){xhr.responseJSON&&xhr.responseJSON.messages?exibirMensagem("error",xhr.responseJSON):exibirMensagem("error",{messages:{error:"Erro desconhecido."}})}})}));